import React from "react";
import axios from "axios";

class App extends React.Component {
  state = {
    isLoading: true,
    movieAxios: [],
  };
  getMovie = async () => {
    const {
      data: {
        data: { movies },
      },
    } = await axios.get(
      "https://yts.mx/api/v2/list_movies.json?sort_by=rating"
    );
    this.setState({ movies, isLoading: false });
  };
  componentDidMount() {
    this.getMovie();
  }

  render() {
